// Helper pour obtenir les challenges par competition
export function getChallengesByCompetition(competitionId: string): Challenge[] {
  if (competitionId === 'advent-of-cyber') {
    return adventChallenges;
  }
  if (competitionId === 'htb-ctf-tryout') {
    return htbTryOutChallenges;
  }
  if (competitionId === 'htb-university-ctf') {
    return universityChallenges;
  }
  if (competitionId === 'scarlet-ctf') {
    return scarletCTFChallenges;
  }
  return [];
}

// Helper pour obtenir un challenge specifique
export function getChallenge(competitionId: string, challengeId: string): Challenge | undefined {
  const challenges = getChallengesByCompetition(competitionId);
  return challenges.find(c => c.id === challengeId);
}
