// Helper pour obtenir les machines par competition
export function getMachinesByCompetition(competitionId: string): Machine[] {
  if (competitionId === 'chisel') {
    return chiselMachines;
  }
  return [];
}

// Helper pour obtenir une machine specifique
export function getMachine(competitionId: string, machineId: string): Machine | undefined {
  const machines = getMachinesByCompetition(competitionId);
  return machines.find(m => m.id === machineId);
}
